思路: // @Title: 全排列 II (Permutations II) // @Author: qisiii // @Date: 2024-09-22 00:23:32 // @Runtime: 2 ms // @Memory: 44.2 MB // @comment: // @flag: class Solution { public List&lt;List&lt;Integer&gt;&gt; permuteUnique(int[] nums) { List&lt;List&lt;Integer&gt;&gt; result = new ArrayList&lt;&gt;(); boolean[] color=new boolean[nums.length]; dfs(result, new ArrayList&lt;&gt;(), nums,color); return result; } private void dfs(List&lt;List&lt;Integer&gt;&gt; result, List&lt;Integer&gt; list, int[] nums,boolean[] color) { if (list.size() == nums.length) { result.add(new ArrayList&lt;&gt;(list)); return; } HashSet&lt;Integer&gt; set=new HashSet&lt;&gt;(); for (int i = 0; i &lt; nums.length; i++) { //选过了 if(color[i]){ continue; } //同轮次中已经处理过这种情况了 if(set.contains(nums[i])){ continue; }else{ set.add(nums[i]); } list.add(nums[i]); color[i]=true; dfs(result, list, nums,color); list.remove(list.size() - 1); color[i]=false; } } } +++ title = &ldquo;全排列 II (Permutations II)&rdquo; draft = false +++
思路: // @Title: 全排列 II (Permutations II) // @Author: qisiii // @Date: 2024-05-10 21:04:47 // @Runtime: 4 ms // @Memory: 44.2 MB // @comment: // @flag: class Solution { public List&lt;List&lt;Integer&gt;&gt; permuteUnique(int[] nums) { Arrays.sort(nums); List&lt;Integer&gt; numList = new ArrayList&lt;&gt;(); for (int i : nums) { numList.add(i); } return permute(numList); } public List&lt;List&lt;Integer&gt;&gt; permute(List&lt;Integer&gt; nums) { List&lt;List&lt;Integer&gt;&gt; result = new ArrayList&lt;&gt;(); if (nums.size() &lt;= 1) { result.add(new ArrayList&lt;&gt;(nums)); return result; } for (int j=0;j&lt;nums.size();j++) { if(j!=0&amp;&amp;nums.get(j)==nums.get(j-1)){ continue; } Integer i=nums.get(j); List&lt;Integer&gt; temp = new ArrayList&lt;&gt;(nums); temp.remove(i); List&lt;List&lt;Integer&gt;&gt; tempList = permute(temp); for (List&lt;Integer&gt; tl : tempList) { tl.add(0, i); } result.addAll(tempList); } return result; } } +++ title = &ldquo;全排列 II (Permutations II)&rdquo; draft = false +++
